import React, { Component } from 'react';
import { Card, CardImg, CardImgOverlay, CardText, CardBody,
    CardTitle } from 'reactstrap';
import Menu from "./MenuComponent";

class DishDetail extends Component {

    constructor(props) {
        super(props);

       console.log("DishDetail component constructor");
    }

    renderDish(dish){
        return (<div className="col-12 col-md-5 m-1">
            <Card>
                <CardImg top src={dish.image} alt={dish.name}/>
                <CardBody>
                    <CardTitle>{dish.name}</CardTitle>
                    <CardText>{dish.description}</CardText>
                </CardBody>
            </Card>
        </div>);
    }

    renderComments(comments){
        const comm = comments.map((item) => {
            console.log("comment:"+item.comment);
            let comment =
                <React.Fragment>
                    <tr><td align="left">{item.comment}</td></tr>
                    <tr><td align="left">--{item.author}, {item.date}</td></tr>

                </React.Fragment>;

            return comment;
        } );

        return <div className="col-12 col-md-5 m-1">
                <h4>Comments</h4>
                <table>
                    <tbody>{comm}</tbody>
                </table>
            </div>;

    }



    render() {
        console.log("DishDetail component render");
        let dish = this.props.selectedDish;
        let selected=
            <div className="row">
                <div className="col-12 col-md-5 m-1">
                    </div>
            </div>;
        if( dish!=null){
            selected =
                <div className="row">
                    {this.renderDish(dish)}
                    {this.renderComments(dish.comments)}
                    {/*<div className="col-12 col-md-5 m-1">*/}
                        {/*<Card>*/}
                            {/*<CardBody>*/}
                                {/*<CardTitle>{dish.name}</CardTitle>*/}
                                {/*<CardText>{dish.description}</CardText>*/}
                            {/*</CardBody>*/}
                        {/*</Card>*/}
                    {/*</div>*/}
             </div>;
        }
        return selected;
    }
}

export default DishDetail;
