import React, { Component } from 'react';
import { Card, CardImg, CardImgOverlay, CardTitle, CardText, CardBody } from 'reactstrap';

class DishDetail extends Component {

    constructor(props) {
        super(props);
    }

    renderDish(dish) {

        if (dish != null) {

            return (
                <Card>
                    <CardImg width="100%" src={dish.image} alt={dish.name}></CardImg>
                    <CardBody>
                        <CardTitle>{dish.name}</CardTitle>
                        <CardText>{dish.description}</CardText>
                    </CardBody >
                </Card>
            );

        } else {

            return (<div></div>);

        };
    }

    renderComments(Comments) {

        if (Comments != null) {

            const DetailComments = Comments.map(
                (Comment) => {
                    return (
                        <ul class="list-unstyled">
                            <div><li>{Comment.comment}</li></div>
                            <li>-- {Comment.author}, {Comment.date} </li>
                        </ul>
                        // <div>1</div>
                    );
                });

            return (
                <div>
                    <h4>Comments</h4>
                    {DetailComments}
                </div>
            )

        } else {

            return (<div></div>);

        };
    }

    render() {

        if (this.props.selectedDish != null) {

            return (
                <div className="row">
                    <div key={this.props.selectedDish.id} className="col-12 col-md-5 m-1">
                        {this.renderDish(this.props.selectedDish)};
                        {/* <Card>
                            <CardImg width="100%" src={this.props.selectedDish.image} alt={this.props.selectedDish.name}></CardImg>
                            <CardBody width="100%">
                                <CardTitle>{this.props.selectedDish.name}</CardTitle>
                                <CardText>{this.props.selectedDish.description}</CardText>
                            </CardBody >
                        </Card> */}
                    </div>
                    <div className="col-12 col-md-5 m-1">
                        {this.renderComments(this.props.selectedDish.comments)}
                    </div>
                </div>
            );
        } else {
            return (<div></div>);
        };

    }
};

export default DishDetail;
